import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import SeatLayout from './SeatLayout';

const Payment = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const bookingDetails = location.state || {};
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';

  // Redirect to login if not logged in
  useEffect(() => {
    if (!isLoggedIn) {
      // Save booking details to return after login
      localStorage.setItem('pendingBooking', JSON.stringify(bookingDetails));
      navigate('/login', { state: { returnUrl: '/payment' } });
    } else {
      // Check if there's a pending booking after login
      const pendingBooking = localStorage.getItem('pendingBooking');
      if (pendingBooking && !bookingDetails.from) {
        const savedBooking = JSON.parse(pendingBooking);
        navigate('/payment', { state: savedBooking, replace: true });
        localStorage.removeItem('pendingBooking');
      }
    }
  }, [isLoggedIn, navigate, bookingDetails]);

  const [paymentMethod, setPaymentMethod] = useState('card');
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [cardDetails, setCardDetails] = useState({
    number: '',
    name: '',
    expiry: '',
    cvv: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedSeats.length < bookingDetails.seats) {
      alert(`Please select ${bookingDetails.seats} seats to continue.`);
      return;
    }

    // Save booking to user's bookings
    const userEmail = localStorage.getItem('userEmail');
    const existingBookings = JSON.parse(localStorage.getItem('userBookings') || '[]');
    const newBooking = {
      ...bookingDetails,
      selectedSeats,
      paymentDetails: {
        method: paymentMethod,
        amount: bookingDetails.amount,
        timestamp: new Date().toISOString()
      },
      userEmail,
      bookingDate: new Date().toISOString(),
      invoiceNumber: `INV${Math.floor(Math.random() * 1000000)}`
    };

    localStorage.setItem('userBookings', JSON.stringify([...existingBookings, newBooking]));

    // Navigate to invoice
    navigate('/invoice', { state: newBooking });
  };

  if (!isLoggedIn) {
    return null; // Don't render anything while redirecting
  }

  return (
    <div className="min-h-screen bg-gray-100 py-8">
      <div className="max-w-3xl mx-auto px-4">
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          <div className="bg-red-600 px-6 py-4">
            <h2 className="text-2xl font-bold text-white">Payment Details</h2>
          </div>

          <div className="p-6">
            {/* Order Summary */}
            <div className="bg-gray-50 rounded-lg p-4 mb-6">
              <h3 className="text-lg font-semibold mb-2">Order Summary</h3>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-gray-600">From</p>
                  <p className="font-semibold">{bookingDetails.from}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">To</p>
                  <p className="font-semibold">{bookingDetails.to}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Date</p>
                  <p className="font-semibold">{bookingDetails.date}</p>
                </div>
                <div>
                  <p className="text-sm text-gray-600">Amount</p>
                  <p className="font-semibold">₹{bookingDetails.amount}</p>
                </div>
              </div>
            </div>

            {/* Seat Layout */}
            <div className="mb-8">
              <SeatLayout 
                selectedSeats={selectedSeats}
                setSelectedSeats={setSelectedSeats}
                totalSeats={bookingDetails.seats}
              />
            </div>

            {/* Payment Methods */}
            <div className="mb-6">
              <h3 className="text-lg font-semibold mb-4">Payment Method</h3>
              <div className="grid grid-cols-3 gap-4">
                <button
                  type="button"
                  className={`p-4 border rounded-lg text-center ${
                    paymentMethod === 'card'
                      ? 'border-red-600 bg-red-50 text-red-600'
                      : 'border-gray-200 hover:border-red-600'
                  }`}
                  onClick={() => setPaymentMethod('card')}
                >
                  <svg className="w-6 h-6 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 10h18M7 15h1m4 0h1m-7 4h12a3 3 0 003-3V8a3 3 0 00-3-3H6a3 3 0 00-3 3v8a3 3 0 003 3z" />
                  </svg>
                  Card
                </button>
                <button
                  type="button"
                  className={`p-4 border rounded-lg text-center ${
                    paymentMethod === 'upi'
                      ? 'border-red-600 bg-red-50 text-red-600'
                      : 'border-gray-200 hover:border-red-600'
                  }`}
                  onClick={() => setPaymentMethod('upi')}
                >
                  <svg className="w-6 h-6 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 8c-1.657 0-3 .895-3 2s1.343 2 3 2 3 .895 3 2-1.343 2-3 2m0-8c1.11 0 2.08.402 2.599 1M12 8V7m0 1v8m0 0v1m0-1c-1.11 0-2.08-.402-2.599-1M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                  </svg>
                  UPI
                </button>
                <button
                  type="button"
                  className={`p-4 border rounded-lg text-center ${
                    paymentMethod === 'netbanking'
                      ? 'border-red-600 bg-red-50 text-red-600'
                      : 'border-gray-200 hover:border-red-600'
                  }`}
                  onClick={() => setPaymentMethod('netbanking')}
                >
                  <svg className="w-6 h-6 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                  </svg>
                  Net Banking
                </button>
              </div>
            </div>

            {/* Card Details Form */}
            {paymentMethod === 'card' && (
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Card Number
                  </label>
                  <input
                    type="text"
                    maxLength="16"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                    placeholder="1234 5678 9012 3456"
                    value={cardDetails.number}
                    onChange={(e) => setCardDetails({ ...cardDetails, number: e.target.value })}
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Cardholder Name
                  </label>
                  <input
                    type="text"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                    placeholder="John Doe"
                    value={cardDetails.name}
                    onChange={(e) => setCardDetails({ ...cardDetails, name: e.target.value })}
                    required
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Expiry Date
                    </label>
                    <input
                      type="text"
                      maxLength="5"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                      placeholder="MM/YY"
                      value={cardDetails.expiry}
                      onChange={(e) => setCardDetails({ ...cardDetails, expiry: e.target.value })}
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      CVV
                    </label>
                    <input
                      type="text"
                      maxLength="3"
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                      placeholder="123"
                      value={cardDetails.cvv}
                      onChange={(e) => setCardDetails({ ...cardDetails, cvv: e.target.value })}
                      required
                    />
                  </div>
                </div>
                <button
                  type="submit"
                  className="w-full bg-red-600 text-white py-3 rounded-lg hover:bg-red-700 transition duration-300"
                >
                  Pay ₹{bookingDetails.amount}
                </button>
              </form>
            )}

            {/* UPI Form */}
            {paymentMethod === 'upi' && (
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    UPI ID
                  </label>
                  <input
                    type="text"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                    placeholder="username@upi"
                    required
                  />
                </div>
                <button
                  type="submit"
                  className="w-full bg-red-600 text-white py-3 rounded-lg hover:bg-red-700 transition duration-300"
                >
                  Pay ₹{bookingDetails.amount}
                </button>
              </form>
            )}

            {/* Net Banking Form */}
            {paymentMethod === 'netbanking' && (
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Select Bank
                  </label>
                  <select
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-red-500"
                    required
                  >
                    <option value="">Select a bank</option>
                    <option value="sbi">State Bank of India</option>
                    <option value="hdfc">HDFC Bank</option>
                    <option value="icici">ICICI Bank</option>
                    <option value="axis">Axis Bank</option>
                  </select>
                </div>
                <button
                  type="submit"
                  className="w-full bg-red-600 text-white py-3 rounded-lg hover:bg-red-700 transition duration-300"
                >
                  Pay ₹{bookingDetails.amount}
                </button>
              </form>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Payment;
