import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import SeatLayout from './SeatLayout';

const Payment = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const bookingDetails = location.state || {};
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const baseFare = 1600; // NPR per seat

  // Redirect to login if not logged in
  useEffect(() => {
    if (!isLoggedIn) {
      localStorage.setItem('pendingBooking', JSON.stringify(bookingDetails));
      navigate('/login', { state: { returnUrl: '/payment' } });
    } else {
      const pendingBooking = localStorage.getItem('pendingBooking');
      if (pendingBooking && !bookingDetails.from) {
        const savedBooking = JSON.parse(pendingBooking);
        navigate('/payment', { state: savedBooking, replace: true });
        localStorage.removeItem('pendingBooking');
      }
    }
  }, [isLoggedIn, navigate, bookingDetails]);

  const handleSeatSelect = (seats) => {
    setSelectedSeats(seats);
    setShowPaymentForm(false); // Reset payment form when seats change
  };

  const handleProceedToPayment = () => {
    if (selectedSeats.length === 0) {
      alert('Please select at least one seat to continue.');
      return;
    }
    setShowPaymentForm(true);
  };

  const handlePayment = (e) => {
    e.preventDefault();
    const totalAmount = baseFare * selectedSeats.length;
    const userEmail = localStorage.getItem('userEmail');

    // Create new booking
    const newBooking = {
      ...bookingDetails,
      selectedSeats,
      paymentDetails: {
        amount: totalAmount,
        timestamp: new Date().toISOString()
      },
      userEmail,
      bookingDate: new Date().toISOString(),
      invoiceNumber: `INV${Math.floor(Math.random() * 1000000)}`
    };

    // Save to user's bookings
    const existingBookings = JSON.parse(localStorage.getItem('userBookings') || '[]');
    localStorage.setItem('userBookings', JSON.stringify([...existingBookings, newBooking]));

    // Navigate to invoice
    navigate('/invoice', { state: newBooking });
  };

  if (!isLoggedIn) return null;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-5xl mx-auto">
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h2 className="text-2xl font-bold mb-4">Bus Details</h2>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <p className="text-gray-600">From</p>
              <p className="font-medium">{bookingDetails.from || 'Kathmandu'} (06:15 pm)</p>
            </div>
            <div>
              <p className="text-gray-600">To</p>
              <p className="font-medium">{bookingDetails.to || 'Pokhara'} (08:45 am)</p>
            </div>
            <div>
              <p className="text-gray-600">Date</p>
              <p className="font-medium">{bookingDetails.date || '2024-03-30'}</p>
            </div>
            <div>
              <p className="text-gray-600">Bus Type</p>
              <p className="font-medium">Deluxe AC Sleeper</p>
            </div>
          </div>
        </div>

        <div className="grid md:grid-cols-3 gap-6">
          <div className="md:col-span-2">
            <SeatLayout onSeatSelect={handleSeatSelect} maxSeats={8} />
          </div>

          <div>
            <div className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-xl font-semibold mb-4">Fare Details</h3>
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span>Basic Fare (per seat)</span>
                  <span>NPR {baseFare.toLocaleString()}</span>
                </div>
                <div className="flex justify-between">
                  <span>Selected Seats</span>
                  <span>{selectedSeats.length}</span>
                </div>
                <div className="border-t pt-3">
                  <div className="flex justify-between font-semibold">
                    <span>Total Amount</span>
                    <span>NPR {(baseFare * selectedSeats.length).toLocaleString()}</span>
                  </div>
                </div>
              </div>

              {!showPaymentForm ? (
                <button
                  onClick={handleProceedToPayment}
                  disabled={selectedSeats.length === 0}
                  className={`w-full mt-6 py-2 px-4 rounded-md text-white font-medium
                    ${selectedSeats.length === 0 
                      ? 'bg-gray-400 cursor-not-allowed' 
                      : 'bg-red-600 hover:bg-red-700'}`}
                >
                  Continue with Selected Seats
                </button>
              ) : (
                <form onSubmit={handlePayment} className="mt-6 space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Card Number
                    </label>
                    <input
                      type="text"
                      maxLength="16"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      placeholder="1234 5678 9012 3456"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Expiry Date
                    </label>
                    <input
                      type="text"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      placeholder="MM/YY"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      CVV
                    </label>
                    <input
                      type="text"
                      maxLength="3"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md"
                      placeholder="123"
                      required
                    />
                  </div>
                  <button
                    type="submit"
                    className="w-full py-2 px-4 bg-red-600 hover:bg-red-700 text-white font-medium rounded-md"
                  >
                    Pay NPR {(baseFare * selectedSeats.length).toLocaleString()}
                  </button>
                </form>
              )}

              <p className="mt-4 text-sm text-gray-500 text-center">
                Selected seats: {selectedSeats.join(', ') || 'None'}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Payment;
