import React, { useState } from 'react';

const PassengerDetails = ({ selectedSeats, onSubmit }) => {
  const [passengers, setPassengers] = useState(
    selectedSeats.map(seat => ({
      seat,
      name: '',
      age: '',
      gender: ''
    }))
  );

  const handleChange = (index, field, value) => {
    const updatedPassengers = [...passengers];
    updatedPassengers[index][field] = value;
    setPassengers(updatedPassengers);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(passengers);
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h3 className="text-xl font-semibold mb-4">Passenger Details</h3>
      <form onSubmit={handleSubmit}>
        {passengers.map((passenger, index) => (
          <div key={passenger.seat} className="mb-6 pb-6 border-b last:border-0">
            <div className="flex items-center mb-3">
              <span className="bg-red-600 text-white px-3 py-1 rounded-md text-sm">
                Seat {passenger.seat}
              </span>
            </div>
            <div className="grid md:grid-cols-3 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Passenger Name
                </label>
                <input
                  type="text"
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  value={passenger.name}
                  onChange={(e) => handleChange(index, 'name', e.target.value)}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Age
                </label>
                <input
                  type="number"
                  required
                  min="1"
                  max="120"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  value={passenger.age}
                  onChange={(e) => handleChange(index, 'age', e.target.value)}
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Gender
                </label>
                <select
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                  value={passenger.gender}
                  onChange={(e) => handleChange(index, 'gender', e.target.value)}
                >
                  <option value="">Select Gender</option>
                  <option value="male">Male</option>
                  <option value="female">Female</option>
                  <option value="other">Other</option>
                </select>
              </div>
            </div>
          </div>
        ))}
        <div className="flex justify-end mt-6">
          <button
            type="submit"
            className="bg-red-600 text-white px-6 py-2 rounded-md hover:bg-red-700"
          >
            Continue to Payment
          </button>
        </div>
      </form>
    </div>
  );
};

export default PassengerDetails;
