import React, { useState, useEffect } from 'react';

const SeatLayout = ({ selectedSeats, setSelectedSeats, totalSeats }) => {
  const [seats, setSeats] = useState([]);

  // Initialize seats
  useEffect(() => {
    const totalRows = 10;
    const seatsPerRow = 4;
    const initialSeats = [];

    for (let row = 0; row < totalRows; row++) {
      const rowSeats = [];
      for (let col = 0; col < seatsPerRow; col++) {
        const seatNumber = row * seatsPerRow + col + 1;
        rowSeats.push({
          number: seatNumber,
          isBooked: Math.random() < 0.3, // 30% seats randomly booked
          isSelected: false
        });
      }
      initialSeats.push(rowSeats);
    }
    setSeats(initialSeats);
  }, []);

  const handleSeatClick = (rowIndex, colIndex) => {
    const seat = seats[rowIndex][colIndex];

    if (seat.isBooked) return; // Can't select booked seats

    const updatedSeats = [...seats];
    const currentSeat = updatedSeats[rowIndex][colIndex];

    if (currentSeat.isSelected) {
      // Deselecting a seat
      currentSeat.isSelected = false;
      setSelectedSeats(prev => prev.filter(s => s !== currentSeat.number));
    } else if (selectedSeats.length < totalSeats) {
      // Selecting a new seat if under limit
      currentSeat.isSelected = true;
      setSelectedSeats(prev => [...prev, currentSeat.number]);
    }

    setSeats(updatedSeats);
  };

  return (
    <div className="max-w-md mx-auto p-4">
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-4">Select Your Seats</h3>
        <div className="flex gap-4 mb-4">
          <div className="flex items-center">
            <div className="w-6 h-6 bg-gray-200 border border-gray-300 rounded mr-2"></div>
            <span className="text-sm">Available</span>
          </div>
          <div className="flex items-center">
            <div className="w-6 h-6 bg-red-600 border border-red-700 rounded mr-2"></div>
            <span className="text-sm">Selected</span>
          </div>
          <div className="flex items-center">
            <div className="w-6 h-6 bg-gray-400 border border-gray-500 rounded mr-2"></div>
            <span className="text-sm">Booked</span>
          </div>
        </div>
      </div>

      <div className="relative">
        {/* Bus Front */}
        <div className="w-20 h-12 bg-gray-300 rounded-t-xl mx-auto mb-4 flex items-center justify-center">
          <span className="text-sm font-medium">Front</span>
        </div>

        {/* Seats */}
        <div className="border border-gray-300 rounded-lg p-4">
          {seats.map((row, rowIndex) => (
            <div key={rowIndex} className="flex justify-between mb-2">
              {row.map((seat, colIndex) => (
                <button
                  key={`${rowIndex}-${colIndex}`}
                  className={`
                    w-10 h-10 rounded-lg flex items-center justify-center text-sm font-medium
                    ${seat.isBooked ? 'bg-gray-400 cursor-not-allowed' : 
                      seat.isSelected ? 'bg-red-600 text-white hover:bg-red-700' : 
                      'bg-gray-200 hover:bg-gray-300'}
                    ${colIndex === 1 ? 'mr-8' : ''}
                  `}
                  onClick={() => handleSeatClick(rowIndex, colIndex)}
                  disabled={seat.isBooked}
                >
                  {seat.number}
                </button>
              ))}
            </div>
          ))}
        </div>
      </div>

      <div className="mt-4">
        <p className="text-sm text-gray-600">
          Selected seats: {selectedSeats.join(', ')}
        </p>
        <p className="text-sm text-gray-600">
          {totalSeats - selectedSeats.length} seats remaining to select
        </p>
      </div>
    </div>
  );
};

export default SeatLayout;
