import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const BusSearch = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const searchData = location.state || {};

  // Mock bus data - in a real app, this would come from an API
  const buses = [
    {
      id: 1,
      name: 'Express Bus',
      departure: '06:00 AM',
      arrival: '12:00 PM',
      price: 800,
      seats: 35
    },
    {
      id: 2,
      name: 'Luxury Bus',
      departure: '08:00 AM',
      arrival: '02:00 PM',
      price: 1200,
      seats: 28
    },
    {
      id: 3,
      name: 'Super Express',
      departure: '10:00 AM',
      arrival: '04:00 PM',
      price: 1000,
      seats: 42
    }
  ];

  const handleBooking = (busId) => {
    navigate('/booking', { state: { ...searchData, busId } });
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <h2 className="text-2xl font-bold mb-4">Search Results</h2>
        <div className="flex justify-between text-gray-600 mb-2">
          <p>From: {searchData.from}</p>
          <p>To: {searchData.to}</p>
          <p>Date: {searchData.date}</p>
        </div>
      </div>

      <div className="space-y-4">
        {buses.map((bus) => (
          <div key={bus.id} className="bg-white rounded-lg shadow-md p-6">
            <div className="flex justify-between items-center">
              <div>
                <h3 className="text-xl font-semibold">{bus.name}</h3>
                <div className="mt-2 text-gray-600">
                  <p>Departure: {bus.departure}</p>
                  <p>Arrival: {bus.arrival}</p>
                </div>
              </div>
              <div className="text-right">
                <p className="text-2xl font-bold text-blue-600">â‚¹{bus.price}</p>
                <p className="text-gray-600">{bus.seats} seats available</p>
                <button
                  onClick={() => handleBooking(bus.id)}
                  className="mt-2 bg-blue-600 text-white px-6 py-2 rounded-md hover:bg-blue-700 transition duration-300"
                >
                  Book Now
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BusSearch;
